INSERT INTO `product` (`id`, `id_category`, `title`, `price_g`, `price_d`, `description`, `characteristic`, `img`, `status`, `id_brand`, `sale`) VALUES
(1, 13, 'Котел Ariston CLAS EVO 24 CF', 10100, 5000, '<p>Котел <strong>Ariston Clas EVO</strong> сочетает в себе высокую способность адаптироваться к требованиям пользователя - через программирования и умение общаться - с реальной экономией энергии. Ассортимент моделей и дополнительного оборудования для контроля и цифровой модуляции способствует возможности запрограммировать котел  таким образом, чтобы получить комфорт, который вы ожидаете в вашем доме.</p>\n<p><strong><em>Особенности</em>&nbsp;</strong></p>\n<ul>\n<li>цифровой индикатор&nbsp;</li>\n<li>эффективный, модулированный насос</li>\n<li>большой теплообменник&nbsp;горячей воды</li>\n<li>предназначен для использования с пультом дистанционного управления clima manager</li>\n<li>система самодиагностики с отображением кодов ошибок</li>\n<li>функция сброса воздуха из системы отопления</li>\n<li>системы антифриза, накопления камня и блокирования насоса</li>\n<li>подготовлен к работе в интегрированных системах, благодаря новым коммуникационным протоколом <em>BUS Bridgenet</em></li>\n</ul>', '                                                                     <tr>\n                                <td class="c1">Встроенный вентилятор  </td>\n                                <td class="c2">нет</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение входа газа  </td>\n                                <td class="c2">3/4''</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение системы ГВС  </td>\n                                <td class="c2">1/2''</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Электрическая мощность  </td>\n                                <td class="c2">79 Вт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Производительность ГВС  </td>\n                                <td class="c2">(ΔT=25/30/35 °C) 14,5/12,2/10,2 л/мин</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение системы отопления  </td>\n                                <td class="c2">3/4''</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Номинальная тепловая мощность в режиме  ЦО / ГВС  </td>\n                                <td class="c2">25,8/11 кВт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Тип монтажа  </td>\n                                <td class="c2">настенный</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Диаметр дымоходной трубы  </td>\n                                <td class="c2">125/130 мм</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Температура отработанных газов  </td>\n                                <td class="c2">118 °С</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Расход газа  </td>\n                                <td class="c2">63,6 кг/ч</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">КПД  </td>\n                                <td class="c2">93 %</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Габариты (ВхШхГ)  </td>\n                                <td class="c2">770х400х315 мм</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Напряжение и частота тока  </td>\n                                <td class="c2">230Вт / 50Гц</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Мощность  </td>\n                                <td class="c2">23,7 кВт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Вес  </td>\n                                <td class="c2">30 кг</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Способ отвода газов  </td>\n                                <td class="c2">дымоходный</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Тип нагрева  </td>\n                                <td class="c2">отопление + горячее водоснабжение</td>\n                            </tr>\n ', '2962', 'Есть в наличии', 6, 1),
(2, 13, 'Котел Ariston CLAS EVO 24 FF', 20200, 1010, '<p>Котел <strong>Ariston Clas EVO</strong> сочетает в себе высокую способность адаптироваться к требованиям пользователя - через программирования и умение общаться - с реальной экономией энергии. Ассортимент моделей и дополнительного оборудования для контроля и цифровой модуляции способствует возможности запрограммировать котел  таким образом, чтобы получить комфорт, который вы ожидаете в вашем доме.</p>\n<p><strong><em>Особенности</em>&nbsp;</strong></p>\n<ul>\n<li>цифровой индикатор&nbsp;</li>\n<li>эффективный, модулированный насос</li>\n<li>большой теплообменник&nbsp;горячей воды</li>\n<li>предназначен для использования с пультом дистанционного управления clima manager</li>\n<li>система самодиагностики с отображением кодов ошибок</li>\n<li>функция сброса воздуха из системы отопления</li>\n<li>системы антифриза, накопления камня и блокирования насоса</li>\n<li>подготовлен к работе в интегрированных системах, благодаря новым коммуникационным протоколом <em>BUS Bridgenet</em></li>\n</ul>', '                                                               <tr>\n                                <td class="c1">Электрическая мощность  </td>\n                                <td class="c2">112 Вт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Производительность ГВС  </td>\n                                <td class="c2">(ΔT =  25/30/35°С) 14,1/12,5/10,4 л/мин</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Номинальная тепловая мощность в режиме  ЦО / ГВС  </td>\n                                <td class="c2">25,8/11 кВт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Тип монтажа  </td>\n                                <td class="c2">настенный</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Диаметр дымоходной трубы  </td>\n                                <td class="c2">60-100 мм / 80-125 мм</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Температура отработанных газов  </td>\n                                <td class="c2">108 °С</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Расход газа  </td>\n                                <td class="c2">57,4 кг/ч</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">КПД  </td>\n                                <td class="c2">94,5 %</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Габариты (ВхШхГ)  </td>\n                                <td class="c2">770х400х315 мм</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Напряжение и частота тока  </td>\n                                <td class="c2">230Вт / 50Гц</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Мощность  </td>\n                                <td class="c2">24,2 кВт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Вес  </td>\n                                <td class="c2">30 кг</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Способ отвода газов  </td>\n                                <td class="c2">турбированный</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Тип нагрева  </td>\n                                <td class="c2">отопление + горячее водоснабжение</td>\n                            </tr>\n                                                                                                                                                                                            <tr>\n                                <td class="c1">Присоединение входа газа  </td>\n                                <td class="c2">3/4''</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Встроенный вентилятор  </td>\n                                <td class="c2">да</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение системы ГВС  </td>\n                                <td class="c2">1/2''</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение системы отопления  </td>\n                                <td class="c2">3/4''</td>\n                            </tr>\n', '2963', 'Есть в наличии', 6, 1),
(5, 19, 'Protherm 20 KLO M - 11,9/17 кВт', 22220, 1110, '<p>Газовые чугунные&nbsp;одноконтурные стационарные котлы 20 KLO M серии Медведь имеют высокий КПД за счет теплообменника сложенного из отдельных звеньев, которые специально сконструированы так, чтобы тепло при сгорании топлива было максимально передано отопительной воде.</p>\n<p>В качестве топлива используется газ природный(магистральный) или сжиженный (баллонный). Чугунные котлы отличаются долговечностью работы теплообменника.</p>\n<p>Котел<strong> Senior Dual KLO состоит из следующих частей:</strong></p>\n<ul>\n<li><span>Чугунный секционный теплообменник, теплоизиляция, гидравлический трубопровод. </span></li>\n<li><span>Панель горелки, оснащена трубками из нержавеющей стали. </span></li>\n<li><span>Форсунки для равномерного смешивания газа с воздухом. </span></li>\n<li><span>Автоматика зажигания HONEYWELL или SIT, соединенная с газовой арматурой. </span></li>\n<li><span>Коллектор продуктов сгорания. </span></li>\n<li><span>Корпус </span></li>\n</ul>\n<p><strong>Комплектация:</strong></p>\n<ul>\n<li><span>Сетевой включатель, индикатор- сигнализация аварийного состояния. </span></li>\n<li><span>Термометр и манометр показывают температуру и давление ОВ. </span></li>\n<li><span>Рабочий термостат - для выбора температуры ОВ. </span></li>\n<li><span>Кнопка RESET - для запуска котла. </span></li>\n<li><span>Автоматика зажигания : HONEYWELL или SIT 537 ABC </span></li>\n<li><span>Переключатель ''лето- зима'', переключатель на пониженную мощность: в положении "зима" обеспечивает подготовку ГВС с приоритетом перед отоплением.<br />В положении "лето" - работа котла только для подготовки горячей воды в бойлере.</span></li>\n<li><span>Аварийный термостат служит для защиты котла от перегрева. </span></li>\n<li><span>Термостат продуктов сгорания SKKT - система контроля тяги дымохода. </span></li>\n<li><span>Термостат насоса ОВ ( производителем настроен t = 0) для запуска насоса после нагрева ОВ в теплообменнике. Рекомендуемая t = 55 C.</span></li>\n</ul>\n<p><strong>Особенности KLO 20</strong></p>\n<p>В случаях, когда нет возможности использовать обычный отвод продуктов сгорания в дымоход, можно использовать вентиляторную надстройку PROTHERM PT - "полу-турбо" - простое дополнение на горловину дымохода для отвода продуктов сгорания через наружную стену</p>', '                                                                <tr>\n                                <td class="c1">Уровень шумов  </td>\n                                <td class="c2">до 55 Дб</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение входа газа  </td>\n                                <td class="c2">G 3/4&quot;</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Присоединение системы отопления  </td>\n                                <td class="c2">G 1&quot;</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Электрическая мощность  </td>\n                                <td class="c2">20 Вт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Номинальная тепловая мощность в режиме  ЦО / ГВС  </td>\n                                <td class="c2">18,5/13 кВт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Тип монтажа  </td>\n                                <td class="c2">напольный</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Диаметр дымоходной трубы  </td>\n                                <td class="c2">130 мм</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Температура отработанных газов  </td>\n                                <td class="c2">100°C</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Расход газа  </td>\n                                <td class="c2">2/1,4 м3/ч</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">КПД  </td>\n                                <td class="c2">90-92 %</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Габариты (ВхШхГ)  </td>\n                                <td class="c2">830х335х600 мм</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Напряжение и частота тока  </td>\n                                <td class="c2">230Вт / 50Гц</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Мощность  </td>\n                                <td class="c2">17 кВт</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Вес  </td>\n                                <td class="c2">90 кг</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Материал  </td>\n                                <td class="c2">чугунные</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Способ отвода газов  </td>\n                                <td class="c2">дымоходный</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Тип нагрева  </td>\n                                <td class="c2">отопление</td>\n                            </tr>\n                                                                                                                        <tr>\n                                <td class="c1">Встроенный вентилятор  </td>\n                                <td class="c2">нет</td>\n                            </tr>\n', '633', 'Есть в наличии', 10, 0);

